import React, { useEffect, useState } from "react";
import {useOutletContext, useParams, Link, useHistory } from "react-router-dom";



function ViewNote(){
    const [deleted, setDeleted] = useState(false);
    const handleDelete = () => {
        setDeleted(true);
    }

    useEffect(() => {
        if (deleted) {
            const updatedNotes = notesObjects.filter(note => note.ID !== id);
            setNotesObjects(updatedNotes);
            history.push('/')
        }

    }, [deleted, history, id, notesObjects, setNotesObjects]);
    const {id} = useParams();
    const [notesObjects, setNotesObjects] = useOutletContext();

    const note = notesObjects.find(note => note.ID === id);
    if (!note) {
        return <div className="noNoteFound">Note not found</div>;
      }
    console.log(note)
    // console.log(notesObjects)
    return(
        <div className="test">

                <div className="editingHeader">
                    <div className='div1'>
                        <div className="viewingHeaderTitle">{note.Title}</div>
                        <div className="viewingHeaderDate">{note.Date}</div>
                    </div>

                    <div className='div2'>
                    <Link key={note.ID} to={'/EditNote/'+note.ID} >
                        <button className="viewingHeaderSave editButton">Edit</button>
                        </Link>
                        <button className="editingHeaderDelete editButton"  onClick={handleDelete}>Delete</button>
                    </div>


                 </div>
                 <div className="content">{note.noteContent}</div>

                 </div>

    )
}

export default ViewNote;
