import React from "react";
import { useOutletContext, useParams, Link, Navigate } from "react-router-dom";

function ViewNote() {
  const { id } = useParams();
  const [notesObjects, setNotes] = useOutletContext();

  const note = notesObjects.find((note) => note.ID === id);
  if (!note) {
    return <div className="noNoteFound">Note not found</div>;
  }

  const handleDelete = () => {
    if (window.confirm("Are you sure you want to delete this note?")) {
      const updatedNotes = notesObjects.filter((note) => note.ID !== id);
      setNotes(updatedNotes);
      if (updatedNotes.length === 0) {
        // Use updatedNotes.length instead of notesObjects.length()
        Navigate("/"); // Navigate to home page when all notes are deleted
      } else {
        Navigate(`/ViewNote/${updatedNotes[0].ID}`);
      }
    }
  };

  return (
    <div className="test">
      <div className="editingHeader">
        <div className="div1">
          <div className="viewingHeaderTitle">{note.Title}</div>
          <div className="viewingHeaderDate">{note.Date}</div>
        </div>

        <div className="div2">
          <Link key={note.ID} to={"/EditNote/" + note.ID}>
            <button className="viewingHeaderSave editButton">Edit</button>
          </Link>
          <button className="editingHeaderDelete editButton" onClick={handleDelete}>
            Delete
          </button>
        </div>
      </div>
      <div className="content">{note.noteContent}</div>
    </div>
  );
}

export default ViewNote;
