import { useRouteMatch } from "react-router-dom";
export default function NoteItem({ ID, Title, Note, NotePreview, noteDate }) {
    const options = {
      year: "numeric",
      month: "long",
      day: "numeric",
      hour: "numeric",
      minute: "numeric",
    };
    const formatDate = (when) => {
      const formatted = new Date(when).toLocaleString("en-US", options);
      if (formatted === "Invalid Date") {
        return "";
      }
      return formatted;
    };

    const match = useRouteMatch("/ViewNote/:id" ||"/EditNote/:id");


    const formattedDate = noteDate ? formatDate(noteDate) : "";

    return (
      <div className={`noteCard ${isActive ? "active" : ""}`}>
        <div className="noteHeading">{Title}</div>
        <div className="noteDate">{formattedDate}</div>
        <div
          className="notePreview"
          dangerouslySetInnerHTML={{ __html: NotePreview }}
        ></div>
      </div>
    );
  }
