// import { Outlet } from "react-router-dom";
// import Sidebar from "./Sidebar.js"
import NoteItem from "./NoteItem.js";
import EditAndSave from "./EditAndSave.js";
import { v4 as uuidv4 } from 'uuid';
import { useEffect, useState } from "react";
import { Link, Outlet, Route, Routes } from "react-router-dom";
import ViewNote from "./ViewNote.js";
// import ViewNote from "./ViewNote.js"

export default function Layout() {
  const [notesObjects, setNotes]= useState([]);

  const [view, setView] = useState(0);

  useEffect(() => {
   function setView(ID){
    <Route path="/ViewNote/${ID}" element = {<ViewNote/>}></Route>
   }
  });

  // javascript 
  function toggleSidebar(){
    document.querySelector(".sideBar").classList.toggle("sideBarHidden");
    document.querySelector(".test").classList.toggle("testExpanded");

  }

  function addNote(){
    // var newHeading = document.getElementsByClassName("editingHeaderTitle")[0].value;
    var newdata = {ID: uuidv4(), Title: "Untitled", noteContent: "...", Date:""}
    setNotes([newdata, ...notesObjects])
  }

  function checkNotes(){
    if (notesObjects.length !== 0 ){
      var NoNotes = document.getElementsByClassName("noNotes")
      NoNotes[0].style.display = "none";
    }
    // console.log(notesObjects.length)
  }
  checkNotes()
  // shows notes contained in json object
  useEffect(() => {
    console.log(notesObjects);

  }, [notesObjects]);


  return (
    <body>
    <nav>

      <button className="sideBarToggle" onClick={toggleSidebar}>&#9776;</button>
      <div className="titleContainer">
        <h1>Lotion</h1>
        <h5>Like Notion, But Worse</h5  >
      </div>
      <div></div>
      {/* <button>&#9681;</button> */}
    </nav>

    <div className="mainContent">
    <div className="sideBar">
            <div className="sideBarHeader">
                <div className="notesTitle">Notes</div>
                <button className="addButton" onClick={addNote}>&#43;</button>
            </div>

            <div className="notes">
              <div className="noNotes" id="noNotes">
                <div></div>
              <div>No Notes Here</div>
              <div></div>
              </div>

                {/* <div className="noteCard">
                    <div className="noteHeading">Untitled</div>
                    <div className="noteDate">Date: 20-07-09</div>
                    <div className="notePreview">Blah Blah Bhah Blah</div>
                    </div> */}


              {notesObjects.map(note=> (
                <Link to={'/ViewNote:'+note.ID} >
                <NoteItem ID = {note.ID} Title = {note.Title} Note = {note.noteContent} Date ={note.Date} onClick={() => setView(note.id)}/>
                </Link>
              ))}
            {/* <NoteItem/> */}

            </div>
            <div></div>
        </div>
      {/* <Sidebar/> */}
      {/* <EditAndSave/> */}
      {/* <div className="noNotesMain">Select a note or create a new one</div> */}
      {/* <ViewNote/> */}
      <Outlet/>
    </div>
    </body>

  )
}
