import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import { useNotesState } from "./Layout";

function ViewNote() {
  const { id } = useParams();
  const [notesObjects] = useNotesState();
  // Find the note with the matching ID
  const note = notesObjects.find((note) => note.ID === id);

  useEffect(() => {
    console.log(note);
  }, [note]);

  return (
    <div className="test">
      <div className="editingHeader">
        <div className="div1">
          <div className="viewingHeaderTitle">{note.Title}</div>
          <div className="viewingHeaderDate">{note.Date}</div>
        </div>

        <div className="div2">
          <button className="viewingHeaderSave editButton">Edit</button>
          <button className="editingHeaderDelete editButton">Delete</button>
        </div>
      </div>
      <div className="content">{note.noteContent}</div>
    </div>
  );
}

export default ViewNote;
