export default function NoteItem({ID, Title, Note, NotePreview, noteDate}){
    const options = {
        year: "numeric",
        month: "long",
        day: "numeric",
        hour: "numeric",
        minute: "numeric",
    };
    const formatted = ""
    const formatDate = (when) => {
        const formatted = new Date(when).toLocaleString("en-US", options);
        if (formatted === "Invalid Date") {
            return "";
        }
        return formatted;
    };
    if (noteDate !== ""){
        formatDate(noteDate)
    }



    return(

        <div className="noteCard">
            <div className="noteHeading">{Title}</div>
            <div className="noteDate">{formatted}</div>
            <div className="notePreview" dangerouslySetInnerHTML={{ __html: NotePreview}}></div>
        </div>    

    )

}
